import React, { useEffect, useState } from 'react';
import styled from 'styled-components'

const Home = () => {
    const [userData , setUserData] = useState({});



    const loadData = async(e)=>{
        const res =await fetch('/home' , { 
            method: 'GET',
            headers:{
                Accept: 'application/json',
                'Content-Type': 'application/json',


            },
            credentials:"include"
        })
        .then((data)=>data.json()).then((d)=>setUserData(d))
    }

    useEffect(()=>{
        loadData();
    },[])
  return (
      <>
      <Container>
            <Welcome>
                <div className="main" > ONLINE VOTING SYSTEM</div>
             
             
                    
                    
            </Welcome>
            
            <LiveFeed>
                
                <Stats>
                    
                <Total>
                
                <h1 style={{color:'blue'}}>{userData.total}</h1>
                </Total>
                <PartyWise>
                
                <Grid>
                  
                    <div className="party">
                        <h1>BJP</h1>
                        <img src="Bjp.jpeg" alt="" />
                        <h2>{userData.bjp}</h2>
                    </div>
                    <div className="party">
                    <h1>AAP</h1>
                        <img src="aap.png" alt="" className="aap"/>
                        <h2>{userData.aap}</h2>
                    </div>
                    <div className="party">
                    <h1>Congress</h1>
                        <img src="congress.png" alt="" />
                        <h2>{userData.congress}</h2>
                    </div>
                    <div className="party">
                    <h1>National Congress Party</h1>
                        <img src="inc.png" alt="" />
                        <h2>{userData.ncp}</h2>
                    </div>
                    <div className="party">
                    <h1>Indian National Lok Dal</h1>
                        <img src="inld.png" alt="" className="aap"/>
                        <h2>{userData.inld}</h2>
                    </div>
                    <div className="party">
                    <h1>JANA SENA PARTY</h1>
                        <img src="janasena.png" alt="" className="aap"/>
                        <h2>{userData.inld}</h2>
                    </div>
                </Grid>
                </PartyWise>
                </Stats>

            </LiveFeed>
      </Container>
      </>
  );
};

export default Home;
const Grid= styled.div`
display:grid;
grid-template-columns:repeat(6,15%);
div{
    box-shadow:1px 2px 5px black;
    height:50vh;
}

@media all and (max-width:1157px){
    grid-template-columns:repeat(2,50%);

}
@media all and (max-width:833px){
    grid-template-columns:1fr;

}

@media all and (max-width:395px){

    .aap{
        width:8rem;
    }
}

`
