import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';


const Login = () => {
    const history = useHistory();

    const [aadhar, setAadhar] = useState('');
    const [password, setPassword] = useState('');

    app.post('/login' , async(req,res)=>{
        try {
            const aadhar = req.body.aadhar;
            const password = req.body.password;
            const user = await Register.findOne({aadhar:aadhar});
            
            // Check if user exists
            if (!user) {
                return res.status(404).send('User not found');
            }
    
            const isMatch =  await bcrypt.compare(password, user.password);
            const token = await user.generateAuthToken();
            
            res.cookie("jwt" , token , {
                expires:new Date(Date.now() + 600000),
                httpOnly:true,
            });
    
            if(isMatch){
                console.log('Login Successful');
                res.status(200).send('Logged in');
            } else {
                console.log('Invalid password');
                res.status(401).send('Invalid password');
            }
        } catch(e){
            console.log(e.message);
            res.status(400).send('Error logging in');
        }
    });
    return (
        <Container>
            <Form>
                <div className="head">Login</div>
                <div className="aadhar">
                    <label htmlFor="aadhar">Aadhar Number</label>
                    <input type="text" name="aadhar" id="aadhar" placeholder="Enter your Aadhar number" value={aadhar} onChange={(e) => setAadhar(e.target.value)} />
                </div>
                <div className="password">
                    <label htmlFor="password">Password</label>
                    <input type="password" name="password" id="password" placeholder="Enter your Password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <button type="button" onClick={LoginUser}>Login</button>
            </Form>
        </Container>
    );
};

export default Login;

const Container = styled.div`
    height: 79vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Form = styled.div`
    height: 60%;
    width: 30%;
    border-radius: 4px;
    box-shadow: 1px 1px 5px black;
    border: 2px solid black;
    padding: 2rem;

    // Media queries...

    form {
        display: flex;
        flex-direction: column;
        gap: 1.4rem;
        height: 100%;

        .password , .aadhar {
            display: flex;
            flex-direction: column;
            padding: 0px 2rem;

            input {
                height: 1.4rem;
            }
        }

        .head {
            height: 17%;
            display: flex;
            justify-content: center;
            align-items: center;
            font-size: 1.8rem;
        }
    }

    button {
        margin: 0px 2rem;
        height: 2rem;
        background: rgba(0,0,0,0.2);
        border: none;
        outline: none;
        cursor: pointer;
        border-radius: 4px;
    }
`;
