import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';

const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

const Login = () => {
  const history = useHistory();
  const [user, setUser] = useState({
    name: '', email: '', mobile: '', password: '', aadhar: '',
  });
  let name, value;

  const handleInput = (e) => {
    name = e.target.name;
    value = e.target.value;
    setUser({ ...user, [name]: value });
  };

  const PostData = async (e) => {
    e.preventDefault();
    const { name, email, mobile, password, aadhar } = user;

    if (name === '' || email === '' || mobile === '' || password === '' || aadhar === '') {
      alert('Fill in all the fields');
      history.push('/signup');
      return;
    }

    if (!emailRegex.test(email)) {
      alert('Invalid email format');
      return;
    }

    if (mobile.length < 10) {
      alert('Mobile number should have at least 10 digits');
      return;
    }

    const res = await fetch('/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name, email, mobile, password, aadhar,
      }),
    });

    const data = await res.json();
    if (res.status === 422 || !data) {
      window.alert('User already exists. Try logging in');
      history.push('/signin');
    } else {
      window.alert('Registration Successful');
      history.push('/signin');
    }
  };

  return (
    <>
      <Container>
        <Form>
          <form method="POST">
            <div className="head">Register</div>

            <div className="name">
              <label htmlFor="name">Name</label>
              <input
                type="text"
                name="name"
                id="name"
                placeholder="Enter your Name"
                value={user.name}
                onChange={(e) => handleInput(e)}
              />
            </div>
            <div className="mobile">
              <label htmlFor="mobile">Mobile Number</label>
              <input
                type="number"
                name="mobile"
                id="mobile"
                placeholder="Enter your Mobile Number"
                value={user.mobile}
                onChange={(e) => handleInput(e)}
              />
            </div>
            <div className="email">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                name="email"
                id="email"
                placeholder="Enter your email address"
                value={user.email}
                onChange={(e) => handleInput(e)}
              />
            </div>

            <div className="password">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                name="password"
                id="password"
                placeholder="Enter your Password"
                value={user.password}
                onChange={(e) => handleInput(e)}
              />
            </div>

            <div className="aadhar">
              <label htmlFor="aadhar">Aadhar Number</label>
              <input
                type="text"
                name="aadhar"
                id="aadhar"
                placeholder="Enter your Aadhar number"
                value={user.aadhar}
                onChange={(e) => handleInput(e)}
              />
            </div>

            <button type="submit" onClick={PostData}>
              Register
            </button>
          </form>
        </Form>
      </Container>
    </>
  );
};

export default Login;
