const Container = styled.div`
  height: 79vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Form = styled.div`
  height: 80%;
  width: 30%;
  border-radius: 4px;
  box-shadow: 1px 1px 5px black;
  border: 2px solid black;
  padding: 2rem;

  // Media queries for responsive design...

  form {
    display: flex;
    flex-direction: column;
    gap: 1.4rem;
    height: 100%;

    .password,
    .email,
    .aadhar,
    .name,
    .mobile {
      display: flex;
      flex-direction: column;
      padding: 0px 2rem;
      input {
        height: 1.4rem;
      }
    }
    .head {
      height: 10%;
      display: flex;
      justify-content: center;
      align-items: center;
      font-size: 1.8rem;
    }
  }

  button {
    margin: 0px 2rem;
    height: 2rem;
    background: rgba(0, 0, 0, 0.2);
    border: none;
    outline: none;
    cursor: pointer;
    border-radius: 4px;
  }

  input[type='number']::-webkit-inner-spin-button,
  input[type='number']::-webkit-outer-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }
`;
