import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const Home = () => {
    const [userData, setUserData] = useState({});

    const loadData = async () => {
        const res = await fetch('/home', {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            credentials: "include"
        });
        const data = await res.json();
        setUserData(data);
    };

    useEffect(() => {
        loadData();
    }, []);

    return (
        <>
            <Container>
                <Welcome>
                    <div className="main">
                        Welcome to the Online Voting System
                    </div>
                    <div className="down">
                        <div className="one">scroll down</div>
                        <div className="two">v</div>
                    </div>
                </Welcome>
                <LiveFeed>
                    <Head>
                        <h1>Live Stats</h1>
                    </Head>
                    <Stats>
                        <Total>
                            <h1>The total votes till date are : </h1>
                            <h1 style={{ color: 'blue' }}>{userData.total}</h1>
                        </Total>
                        <PartyWise>
                            <h1 className="partyWise">PartyWise vote are : </h1>
                            <Grid>
                                {/* Party-wise vote grid goes here */}
                            </Grid>
                        </PartyWise>
                    </Stats>
                </LiveFeed>
            </Container>
        </>
    );
};

export default Home;

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
`;

const Welcome = styled.div`
    width: 50%;
    background: rgba(0, 0, 0, 0.2);
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const LiveFeed = styled.div`
    width: 50%;
    background: white;
`;

const Grid = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 33.33%);
    div {
        box-shadow: 1px 2px 5px black;
        height: 50vh;
    }
    @media all and (max-width: 1157px) {
        grid-template-columns: repeat(2, 50%);
    }
    @media all and (max-width: 833px) {
        grid-template-columns: 1fr;
    }
    @media all and (max-width: 395px) {
        .aap {
            width: 8rem;
        }
    }
`;

const PartyWise = styled.div`
    width: 100vw;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 4rem;
    .partyWise {
        height: 10vh;
        @media all and (max-width: 500px) {
            font-size: 1.4rem;
        }
    }
    div {
        margin: 1rem;
        overflow: hidden;
        text-align: center;
        border: 1px solid black;
        padding: 2rem;
        h1 {
            height: 40%;
            font-size: 1.3rem;
        }
        img {
            margin-bottom: 2rem;
            height: 40%;
        }
        h2 {
            height: 40%;
        }
    }
    @media all and (max-width: 537px) {
        padding: 0rem;
    }
`;

const Total = styled.div`
    width: 100vw;
    height: 20vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;
`;

const Head = styled.div`
    width: 100%;
    height: 20vh;
    display: flex;
    font-size: 2.5rem;
    justify-content: center;
    color: red;
    align-items: center;
    @media all and (max-width: 720px) {
        font-size: 2rem;
    }
    @media all and (max-width: 400px) {
        font-size: 1.7rem;
    }
    @media all and (max-width: 305px) {
        font-size: 1.5rem;
    }
`;

const Stats = styled.div``;
